#include <assert.h>

int main() {
    // Tests RLE
    assert(strcmp(RLE(""), "") == 0);
    assert(strcmp(RLE("abc"), "1a1b1c") == 0);
    assert(strcmp(RLE("abbccc"), "1a2b3c") == 0);
    assert(strcmp(RLE("aaabaa"), "3a1b2a") == 0);
    assert(strcmp(RLE("aAa"), "1a1A1a") == 0);
    assert(strcmp(RLE("WWWWWWWWWWWWW"), "9W4W") == 0);

    printf("===============\nTest RLE passé\n");

    // Tests RLE récursif
    char* saeIte20 = " ";

    assert(strcmp(RLE_rec("", 1), "") == 0);
    assert(strcmp(RLE_rec("", 3), "") == 0);
    assert(strcmp(RLE_rec("abc", 1), "1a1b1c") == 0);
    assert(strcmp(RLE_rec("abbccc", 1), "1a2b3c") == 0);
    assert(strcmp(RLE_rec("aaabaa", 1), "3a1b2a") == 0);
    assert(strcmp(RLE_rec("aAa", 1), "1a1A1a") == 0);
    assert(strcmp(RLE_rec("abc", 2), "111a111b111c") == 0);
    assert(strcmp(RLE_rec("abc", 3), "311a311b311c") == 0);
    assert(strcmp(RLE_rec("SAE Algo", 20), saeIte20) == 0);

    printf("===============\nTest RLE-rec passé\n");

    // Tests unRLE
    assert(strcmp(unRLE(""), "") == 0);
    assert(strcmp(unRLE("1a1b1c"), "abc") == 0);
    assert(strcmp(unRLE("1a2b3c"), "abbccc") == 0);
    assert(strcmp(unRLE("3a1b2a"), "aaabaa") == 0);
    assert(strcmp(unRLE("1a1A1a"), "aAa") == 0);
    assert(strcmp(unRLE("9W4W"), "WWWWWWWWWWWWW") == 0);

    printf("===============\nTest unRle passé\n");

    // Tests unRLE récursif
    assert(strcmp(unRLE_rec("", 1), "") == 0);
    assert(strcmp(unRLE_rec("", 3), "") == 0);
    assert(strcmp(unRLE_rec("1a1b1c", 1), "abc") == 0);
    assert(strcmp(unRLE_rec("1a2b3c", 1), "abbccc") == 0);
    assert(strcmp(unRLE_rec("3a1b2a", 1), "aaabaa") == 0);
    assert(strcmp(unRLE_rec("1a1A1a", 1), "aAa") == 0);
    assert(strcmp(unRLE_rec("111a111b111c", 2), "abc") == 0);
    assert(strcmp(unRLE_rec("311a311b311c", 3), "abc") == 0);

    printf("===============\nTest unRle_rec passé\n");

    // Vos tests personnalisés
    assert(strcmp(RLE("xxxxx"), "5x") == 0);
    assert(strcmp(RLE("aAaAaA"), "1a1A1a1A1a1A") == 0);
    assert(strcmp(RLE("abcd"), "1a1b1c1d") == 0);
    assert(strcmp(RLE("abbaaacc"), "1a2b3a2c") == 0);
    assert(strcmp(RLE("a"), "1a") == 0);

    printf("===============\nTous les tests RLE personnalisés sont passés.\n");

    assert(strcmp(unRLE("5x"), "xxxxx") == 0);
    assert(strcmp(unRLE("1a1A1a1A1a1A"), "aAaAaA") == 0);
    assert(strcmp(unRLE("1a1b1c1d"), "abcd") == 0);
    assert(strcmp(unRLE("2a2b3a2c"), "abbaaacc") == 0);
    assert(strcmp(unRLE("1a"), "a") == 0);
    assert(strcmp(unRLE("2a2b2c2d"), "aabbccdd") == 0);

    printf("===============\nTous les tests unRLE personnalisés sont passés.\n");

    // Tests unRLE récursif
    assert(strcmp(unRLE_rec("151x", 2), "xxxxx") == 0);
    assert(strcmp(unRLE_rec("111a111b111c111d", 2), "abcd") == 0);

    printf("===============\nTous les tests unRLE_rec personnalisés sont passés.\n");

    // Tests RLE récursif
    assert(strcmp(RLE_rec("xxxxx", 2), "151x") == 0);
    assert(strcmp(RLE_rec("abcd", 2), "111a111b111c111d") == 0);

    printf("===============\nTous les tests RLE_rec personnalisés sont passés.\n");

    printf("===============\nTous les tests sont passés.\n");

    return 0;
}