package iut.sae;

import org.junit.Test;

import junit.framework.TestCase;


public class AlgoTest extends TestCase{

/*
 * RLE("")=""
 * RLE("abc")="1a1b1c"
 * RLE("abbccc")="1a2b3c"
 * RLE("aaabaa")="3a1b2a"
 * RLE("aAa")="1a1A1a"
 */
   @Test
   public void testRLE() {
      assertEquals("", MonAlgo.RLE("") );
      
      assertEquals("1a1b1c", MonAlgo.RLE("abc"));
      assertEquals("1a2b3c", MonAlgo.RLE("abbccc"));
      assertEquals("3a1b2a", MonAlgo.RLE("aaabaa"));
      assertEquals("1a1A1a", MonAlgo.RLE("aAa"));
      assertEquals("9W4W", MonAlgo.RLE("WWWWWWWWWWWWW"));

   }


   /*
   * RLE(str, 1)=RLE(str)
   * RLE(str, 3)=RLE(RLE(RLE(str)))
   */
   @Test
   public void testRLERecursif(){
      try{
         assertEquals("", MonAlgo.RLE("", 1));
         assertEquals("", MonAlgo.RLE("", 3));
         
         assertEquals("1a1b1c", MonAlgo.RLE("abc", 1));
         assertEquals("1a2b3c", MonAlgo.RLE("abbccc", 1));
         assertEquals("3a1b2a", MonAlgo.RLE("aaabaa", 1));
         assertEquals("1a1A1a", MonAlgo.RLE("aAa", 1));

         assertEquals("111a111b111c", (MonAlgo.RLE("abc", 2)));
         assertEquals("311a311b311c", (MonAlgo.RLE("abc", 3)));

         String saeIte20=" ";
         assertEquals(saeIte20, MonAlgo.RLE("SAE Algo", 20));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }
   


   /*
  * unRLE(RLE(str))=str
  *
  * unRLE("")=""
  * unRLE("1a1b1c")="abc"
  * unRLE("1a2b3c")="abbccc"
  * unRLE("3a1b2a")="aaabaa"
  * unRLE("1a1A1a")="aAa"
  */

   @Test
   public void testUnRLE(){
      try{
         assertEquals("", MonAlgo.unRLE(""));
         assertEquals("abc", MonAlgo.unRLE("1a1b1c"));
         assertEquals("abbccc", MonAlgo.unRLE("1a2b3c"));
         assertEquals("aaabaa", MonAlgo.unRLE("3a1b2a"));
         assertEquals("aAa", MonAlgo.unRLE("1a1A1a"));
         assertEquals("WWWWWWWWWWWWW", MonAlgo.unRLE("9W4W"));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }
   
   @Test
   public void testUnRLERecursif(){
      try{
         assertEquals("", MonAlgo.unRLE("", 1));
         assertEquals("", MonAlgo.unRLE("", 3));
         
         assertEquals("abc", MonAlgo.unRLE("1a1b1c", 1));
         assertEquals("abbccc", MonAlgo.unRLE("1a2b3c", 1));
         assertEquals("aaabaa", MonAlgo.unRLE("3a1b2a", 1));
         assertEquals("aAa", MonAlgo.unRLE("1a1A1a", 1));

         assertEquals("abc", (MonAlgo.unRLE("111a111b111c", 2)));
         assertEquals("abc", (MonAlgo.unRLE("311a311b311c", 3)));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }

   @Test
   public void testMesTests(){
      try{
         //Pour les tests RLE
         assertEquals("5x", MonAlgo.RLE("xxxxx"));
         assertEquals("1a1A1a1A1a1A", MonAlgo.RLE("aAaAaA"));
         assertEquals("1a1b1c1d", MonAlgo.RLE("abcd"));
         assertEquals("1a2b3a2c", MonAlgo.RLE("abbaaacc"));
         assertEquals("1a", MonAlgo.RLE("a"));


         //pour les tests unRLE
         assertEquals("xxxxx", MonAlgo.unRLE("5x"));
         assertEquals("aAaAaA", MonAlgo.unRLE("1a1A1a1A1a1A"));
         assertEquals("abcd", MonAlgo.unRLE("1a1b1c1d"));
         assertEquals("aabbaaacc", MonAlgo.unRLE("2a2b3a2c"));
         assertEquals("a", MonAlgo.unRLE("1a"));
         assertEquals("aabbccdd", MonAlgo.unRLE("2a2b2c2d"));

         //pour les tests unRLE récurcif
         assertEquals("xxxxx", MonAlgo.unRLE("151x", 2));
         assertEquals("abcd", MonAlgo.unRLE("111a111b111c111d", 2));

         //pour les tests RLE récurcif
         assertEquals("151x", MonAlgo.RLE("xxxxx", 2));
         assertEquals("111a111b111c111d", MonAlgo.RLE("abcd", 2));


      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }

}
